import classes from './MealItemForm.module.css';
import Input from '../../UI/Input';
import { useRef ,  useState } from 'react';
const MealItemForm = props =>
{
    const [amountIsValid , setAmountIsValid] = useState(false);

    const submitHandler = event =>
    {
        event.preventDefault();

        const enteredAmount = amountInputRef.current.value;
        const enteredAmountNumber = +enteredAmount;

        if (
            enteredAmount.trim().length === 0 ||
            enteredAmountNumber < 1 ||
            enteredAmountNumber > 5
          ) {
            setAmountIsValid(false);
            return;
          }
      

    };

    //   // <input />  ...Earlier We wrote this above button a=but for convenience as Card can be used many times in the poroject
    // Input can also be used.. Hence for better practise We male input as a separate Component insie UI.
    return(

     <form className={classes.form}>
        <input label="Amount" input={{
            id: 'amount' ,
            type: 'number' ,
            min: '1' ,
            max: '5',
            step :'1',
            defaultValue: '1'
        }} />
    
        <button>+ Add</button>
    </form>
    );
};

export default MealItemForm;
